import React from "react";

const Comment = ({ comments }) => {
  return comments.map(item => {
    return (
      <div key={item.id}>
          <ul>
                <li>{item.name}</li>
          </ul>
      </div>
    );
  });

};

const Section = ({section }) =>{

    return section.map(item => {

        return (
            <div>
                    <select>
    <option value="nomdepays">ordre alphabetique par nom de pays</option>
    <option value="nomdepaysinverse">ordre alphabetique inverse par nom de pays</option>
    <option value="capitale">ordre alphabetique par nom de capitale</option>
    <option value="capitaleinverse">ordre alphabetique inverse par nom de capital </option>
</select>
            </div>

        );
    });
};





export default Comment;
