import React from "react";

const Comment = ({ comments }) => {
  return comments.map(item => {
    return (
      <div key={item.id}>
          <ul>
                <li>{item.name}</li>
          </ul>
      </div>
    );
  });
};

<select>
    <option value="nomdepays">ordre alphabetique par nom de pays</option>
    <option value="nomdepaysinverse">ordre alphabetique par nom de pays inverse</option>
    <option value="capitale">ordre alphabetique par nom de pays</option>
    <option value="capitaleinverse">ordre alphabetique par nom de pays</option>
</select>



export default Comment;
